import React, { useState } from 'react';
import { Select, Button, FileInput } from 'shadecn-ui'; // Adjust import based on actual library structure

function App() {
  const [selectedTemplate, setSelectedTemplate] = useState('');
  const [file, setFile] = useState(null);

  const handleTemplateChange = (e) => {
    setSelectedTemplate(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDownload = () => {
    // Handle file download logic
    console.log('Download file');
  };

  return (
    <header className='h-screen flex items-center py-8 container'>
      <div className="w-full grid gap-4 content-start items-center justify-center">
        <div className='text-center'>
          <h1 className="text-3xl font-bold">Select</h1>
          <p>Select the template you wish to use...</p>
          <Select 
            className="my-4" 
            value={selectedTemplate} 
            onChange={handleTemplateChange}
            options={[
              { value: '', label: 'Select an option' },
              { value: 'proposal', label: 'Proposal' },
              { value: 'quote', label: 'Quote' },
              { value: 'invoice', label: 'Invoice' },
              { value: 'one-off', label: 'One-Off' },
            ]}
          />
          <Button onClick={handleDownload} className="mt-4">Download Final File</Button>
        </div>
        <div className="w-full mt-8 text-center">
          <label htmlFor="file-upload" className="block text-lg font-medium">
            BigTime PDF
          </label>
          <FileInput 
            id="file-upload" 
            className="border-dashed border-2 border-gray-300 p-4 mt-2"
            onChange={handleFileChange}
          >
            {file ? file.name : 'Click or Drop File Here'}
          </FileInput>
        </div>
      </div>
    </header>
  );
}

export default App;
